			var columnIndex = this.grid.columns.get(field, 'index');
			return this.onEndEditCell({ rowId: rowId, column: columnIndex});
		});
		dojo.connect(grid, 'gridKeyPress', this, 'handleGridKeyPress');
		this.grid = grid;

		this.initialize();
