Item.method_code:
//debugger;
var element = document.getElementsByName("is_template")[0];
var isChecked = false;
if (element) {isChecked=element.checked;}

var htmlTemplIndicator = document.getElementById("is_folder_template");
if (htmlTemplIndicator) {
  var parentElem = htmlTemplIndicator.parentNode;
  if (htmlTemplIndicator && parentElem) {
    if (!isChecked) {parentElem.removeChild(htmlTemplIndicator);}
  }
}
return;
