//--- Rola added
FolderStructGrid.prototype.setGridRowUserData = function FolderStructGrid_setGridRowUserData(rowId, dataName, dataValue) {
  if (!rowId || rowId === "") {return;}
  switch(dataName)
  {
  case "gridData_rowItemType":
  	this.GridRowUserData[rowId] = {
	  itemType: dataValue
	};
    break;
  case "gridData_rowItemId":
  	this.GridRowUserData[rowId] = {
	  itemId: dataValue
	};
    break;
  }
};

//--- Rola added
FolderStructGrid.prototype.getGridRowUserData = function FolderStructGrid_getGridRowUserData(rowId, dataName) {
  if (!rowId || rowId === "") {return;}
  var retValue = "";
  switch(dataName)
  {
    case "gridData_rowItemType":
	  return this.GridRowUserData[rowId].itemType;

    case "gridData_rowItemID":
    case "gridData_rowItemId":
	  return this.GridRowUserData[rowId].itemId;
  }
};

//------ Definition of default grid context menu
FolderStructGrid.prototype.initDefaultMenu = function FolderStructGrid_initDefaultMenu() {
	var defaultMenu = [];
//  defaultMenu.push({name: "view_item", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.view_item")});
//	defaultMenu.push({separator: true });

    defaultMenu.push({name: "view_folder", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.view_folder")});
	if (this.arasRelease !== "Aras9") {
      defaultMenu.push({name: "view_attached_files", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.view_attached_files")});
    }

	defaultMenu.push({name: "refresh_this_row", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.refresh_this_row")});

	if(this.isEditable()) {
 	  defaultMenu.push({separator: true });
	  defaultMenu.push({name: "lock_row_item", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.lock_row_item")});
	  defaultMenu.push({name: "unlock_row_item", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.unlock_row_item")});
 	  defaultMenu.push({separator: true });
	  defaultMenu.push({name: "add_new_controlled_item", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.add_new_controlled_item")});
	  defaultMenu.push({name: "add_existing_controlled_item", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.add_existing_controlled_item")});
	  defaultMenu.push({name: "add_new_file_to_folder", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.add_new_file_to_folder")});
	  defaultMenu.push({name: "add_sub_folder", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.add_sub_folder")});
	}

	if (this.currentUserIsOwner) {
 	  defaultMenu.push({separator: true });
	  defaultMenu.push({name: "promote_folders_to_active_down", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.promote_folders_to_active_down")});
	  defaultMenu.push({name: "promote_folders_to_archived_down", label: top.aras.getResource("ItemFolders","itemfoldertreegrid.contextmenu.promote_folders_to_archived_down")});
	}

	return defaultMenu;
};

//------ BEGIN: local action handlers for menu or toolbar clicks
fn_onToolbarAndMenuClickLocalAction = function (folderGridCtxt, actionId, actionSource) {
   var selectedId = folderGridCtxt.grid.getSelectedId(); //selected grid row
   if (actionSource === "menu") {
    if (!selectedId || typeof(selectedId) == "undefined" || selectedId === "" || selectedId === "NOTHING_MSG") {
       return;
	 }
   }

   var rowItemType = folderGridCtxt.getGridRowUserData(selectedId,"gridData_rowItemType");
   var rowItemId = folderGridCtxt.getGridRowUserData(selectedId,"gridData_rowItemID");

   var checkMsg = fn_ValidateActionRequest(folderGridCtxt, actionId, rowItemType, selectedId);
   if (checkMsg !== "") {top.aras.AlertError(checkMsg);return;}

   //debugger;
   var eArg = {selectedId: selectedId, rowItemType: rowItemType, rowItemId: rowItemId};

   actionId = actionSource + "." + actionId;
   // NOTE: toolbar actions: expand, collapse, refresh are covered by default handler
	switch(actionId)
	{
	  case "toolbar.execute_search":
	     alert("The search feature is not implemented, yet!");
	  	 break;
	  case "toolbar.view_item":
	  case "menu.view_item":
	     fn_onOpenRowItem(folderGridCtxt, eArg);
	  	 break;
	  case "menu.refresh_this_row":
	  	 fn_enableToolbarControls(folderGridCtxt.toolbar, false, folderGridCtxt.fixEffectivityToReleased);
	     fn_onRefreshThisFolder(folderGridCtxt, eArg);
	  	 fn_enableToolbarControls(folderGridCtxt.toolbar, true, folderGridCtxt.fixEffectivityToReleased);
	  	 break;
	  case "menu.view_folder":
	     fn_onOpenRowItem(folderGridCtxt, eArg, "Item Folder");
	  	 break;
	  case "menu.view_attached_files":
	     fn_onViewRelatedFiles(folderGridCtxt, eArg);
	  	 break;
	  case "menu.promote_folders_to_active_down":
	     fn_onPromoteFoldersDown(folderGridCtxt, eArg, "Active");
	  	 break;
	  case "menu.promote_folders_to_archived_down":
	     fn_onPromoteFoldersDown(folderGridCtxt, eArg,"Archived");
	  	 break;
	  case "menu.lock_row_item":
	     fn_onLockRowItem(folderGridCtxt, eArg);
	  	 break;
	  case "menu.unlock_row_item":
	     fn_onUnLockRowItem(folderGridCtxt, eArg);
	  	 break;
	  case "menu.add_new_controlled_item":
	     fn_onAddControlledItem(folderGridCtxt, eArg, true);
	  	 break;
	  case "menu.add_existing_controlled_item":
	     fn_onAddControlledItem(folderGridCtxt, eArg, false);
	  	 break;
	  case "menu.add_new_file_to_folder":
	     fn_onAddFileToFolder(folderGridCtxt, eArg);
	  	 break;
	  case "menu.add_sub_folder":
	     fn_onAddSubFolder(folderGridCtxt, eArg);
	  	 break;

//	  case "menu.xx":
//	  	 break;
	}
};

//-----------------
fn_onOpenRowItem = function (folderGridCtxt, eArg, checkForItemType) {
  //debugger;
  var itemId = eArg.rowItemId;
  var itemType = eArg.rowItemType;

  // phantom rows cannot be opened - open parent folder instead
  if (itemType === folderGridCtxt.constants.folderGroupCtrldItemsRowType || itemType === folderGridCtxt.constants.folderGroupFilesRowType){
    selectedId = folderGridCtxt.grid.getParentId(eArg.selectedId);
    top.aras.uiShowItem(folderGridCtxt.getGridRowUserData(eArg.selectedId,"gridData_rowItemType"), folderGridCtxt.getGridRowUserData(eArg.selectedId,"gridData_rowItemID"));
    return;
  }
  // if check option set, check if row is of correct type
  if (checkForItemType && checkForItemType !== "")
  {
    if (checkForItemType.indexOf(itemType) < 0)
    {
      top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_rows_of_this_type"));
      return;
    }
  }
  // if Folder row, just open the row item
  if (itemType === "Item Folder"){top.aras.uiShowItem(itemType, itemId);return;}

  // files and controlled items are versionable
  //item version might have changed since grid was loaded, get right version of item based on effectivity mode
  var itm = null;
  var selectProps = "";
  if (itemType === "File") {selectProps = "filename,generation,modified_on,comments";}
  else {selectProps = "id,config_id,generation,is_released,is_current,item_number,name,major_rev,state,locked_by_id,owned_by_id,managed_by_id,team_id,modified_on,description,has_files,has_files_icon,itemtype";}

  switch (fn_getChoiceSelectionOnToolbar(folderGridCtxt.toolbar,"effectivity_mode"))
  {
    case "current_config":   
//      itm = fn_GetItemsOfReleasedGeneration(itemType,"'0680E81AEFCB46ACA36D90A673C6ACBB','D45072B9014740F1A797D0597C3EA112','237546E10EF745C8B9499C248ADBC188'",selectProps);
//      debugger;
      itm = fn_GetItemsOfCurrentGeneration(itemType,itemId,selectProps);
      if (itm.isError()) {top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.get_item_current_generation_error") + itm.getErrorString());return;}
      break;
    case "latest_released":
      itm = fn_GetItemsOfReleasedGeneration(itemType,itemId,selectProps);
      if (itm.isError()) {top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.get_item_released_generation_error") + itm.getErrorString());return;}
      break;
  }

  //update grid data of controlled item, if id had changed
  var newId = itm.getID();
  if (itm && itemId !== newId ) {

  	folderGridCtxt.UpdateFolderControlledItemRowValues(eArg.selectedId, itm, null);
    itemId=newId;
  }
  top.aras.uiShowItem(itemType, itemId);
};

fn_onRefreshThisFolder = function (folderGridCtxt, eArg) {
  if (eArg.rowItemType !== "Item Folder"){top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_valid_on_folder_rows"));return;}

  fn_enableToolbarControls(folderGridCtxt.toolbar, false, folderGridCtxt.fixEffectivityToReleased);
  folderGridCtxt.grid.SetPaintEnabled(false);

  // delete all Children down from this node (selectedId)
  var allChildIds = folderGridCtxt.grid.getChildItemsId(eArg.selectedId,true,"|").split("|");
  //## getChildItemsId  method not available in dojo version of grid (Aras10)

  for (var i=0;i<allChildIds.length;i++)
  {
    folderGridCtxt.grid.deleteRow(allChildIds[i]);
    delete folderGridCtxt.getGridRowUserData[allChildIds[i]];
  }

  var isReleased = (fn_getChoiceSelectionOnToolbar(folderGridCtxt.toolbar,"effectivity_mode") === "latest_released");

  //reload this folder item with structure details and the re-draws the grid rows
  var thisFolderStructConfig = folderGridCtxt.GetFullFolderStructConfig(eArg.rowItemId, isReleased, folderGridCtxt.gridEmptyTable_xml);
  if (thisFolderStructConfig.getItemCount() === 0) {return;} // do nothing 


  var parentRowId = folderGridCtxt.grid.getParentId(eArg.selectedId);

  // is it the root folder ?
  if (!parentRowId || parentRowId === undefined) {
  	folderGridCtxt.UpdateFolderRowValues(eArg.selectedId, thisFolderStructConfig, null);
  }
  else {
    // reload relationship to parent
	var parentId=folderGridCtxt.getGridRowUserData(parentRowId,"gridData_rowItemID");
    var parentFolderRel = top.aras.newIOMItem("Item Sub Folder","get");
    parentFolderRel.setAttribute("select",folderGridCtxt.folderRelItemSelectProperties+",related_id"); 
    parentFolderRel.setProperty("source_id",parentId);
    parentFolderRel.setProperty("related_id",eArg.rowItemId);
    parentFolderRel = parentFolderRel.apply();
    if (parentFolderRel.isError) {top.aras.AlertError(parentFolderRel.getErrorString()); return;}

    folderGridCtxt.UpdateFolderRowValues(eArg.selectedId, thisFolderStructConfig, parentFolderRel);
  } 
  var folderItem = thisFolderStructConfig.getItemByIndex(0);

  // draw new related data of this folder
  folderGridCtxt.DrawFolderControlledItemRows(folderItem, eArg.selectedId);
  folderGridCtxt.DrawFolderFileRows(folderItem, eArg.selectedId);

  // draw new children with related data
  var parents = {};
  folderGridCtxt.DrawFolderChildren(folderItem, folderItem.getID(), eArg.selectedId, parents);
  folderGridCtxt.grid.openItem(eArg.selectedId); //refreshed folder Item

  folderGridCtxt.grid.SetPaintEnabled(true);
  fn_enableToolbarControls(folderGridCtxt.toolbar, true, folderGridCtxt.fixEffectivityToReleased);
  return;
};

fn_onViewRelatedFiles = function (folderGridCtxt, eArg) {
  //debugger;
  var argsItem = innovator.newItem(eArg.rowItemType,"get");
  argsItem.setID(eArg.rowItemId);

  var result = top.aras.evalMethod ("Item Folder ViewItemFiles Action",argsItem.dom.xml);
  if (result.isError ()) {top.aras.AlertError (top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.view_item_files_error") + result);} 
  return;
};

fn_onPromoteFoldersDown = function (folderGridCtxt, eArg, targetState) {
  if (eArg.rowItemType !== "Item Folder"){top.aras.AlertError(top.aras.getResource("ItemFolders","itemfoldertreegrid.message.action_not_valid_on_folder_rows"));return;}

  var lockStatus = fn_GetLockedStatusOfItemFromServer(eArg.rowItemType, eArg.rowItemId); 
  if (lockStatus > 0) {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_locked_by_you_or_other_please_unlock"));
    return;
  }

  if (targetState === "Archived"){if(!confirm(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.confirm_promote_to_archived"))){return;}}
  if (targetState === "Active")  {if(!confirm(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.confirm_promote_to_active"))){return;}}

  var argsItem = innovator.newItem(eArg.rowItemType,"get");
  argsItem.setAttribute("select","id,name,state,owned_by_id,managed_by_id,team_id");
  argsItem.setID(eArg.rowItemId);
  argsItem = argsItem.apply();

  var folderCurState = argsItem.getProperty("state","");
  if (folderCurState === targetState) {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_already_in_status_x")+targetState+" !");
    return;
  }
  if (targetState === "Active" && folderCurState !== "Planning") {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_must_be_in_status_planning"));
    return;
  }
  if (targetState === "Archived" && folderCurState !== "Active") {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_must_be_in_status_active"));
    return;
  }

  argsItem.setProperty("state",targetState); // state to promote to
  top.aras.evalMethod ("Item Folders Promote DownA",argsItem.dom.xml);

  return;
};

fn_onLockRowItem = function (folderGridCtxt, eArg) {
  var lockStatus = fn_GetLockedStatusOfItemFromServer(eArg.rowItemType, eArg.rowItemId);

  if (lockStatus > 0) {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.already_locked"));
  }
  else {
    // lock the item an report error, if failed
    var res = fn_LockThisItem(eArg.rowItemType, eArg.rowItemId);
    if (res.isError()) {top.aras.AlertError(res.getErrorString());return;}

    // update lock cell of row 
    var col = folderGridCtxt.lockIconColumnNo;
    folderGridCtxt.gridSetCellValue(eArg.selectedId,col,fn_GetLockedCellFormatByStatusCode(folderGridCtxt,1));
  }
  return;
};

fn_onUnLockRowItem = function (folderGridCtxt, eArg) {
  var lockStatus = fn_GetLockedStatusOfItemFromServer(eArg.rowItemType, eArg.rowItemId);

  if (lockStatus === 0) {
    top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.item_not_locked"));
  }
  else {
    if (lockStatus > 1) {top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.cannot_unlock_locked_by_other"));return;}
    // unlock the rowItem
    var res = fn_UnLockThisItem(eArg.rowItemType, eArg.rowItemId);
    if (res.isError()) {top.aras.AlertError(top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.unlock_error") + res.getErrorString());return;}

    // update lock cell of row 
    folderGridCtxt.gridSetCellValue(eArg.selectedId,folderGridCtxt.lockIconColumnNo,"");
  }
  return;
};
//------ END: local action handlers for menu or toolbar clicks

//------ BEGIN: validation functions
fn_checkRowItemIsLocked = function (folderGridCtxt, selectedId, itemType, itemId, gridLockColumn) {
  // return error msg or "" if lockStatus OK.
  var lockStatus = fn_GetLockedStatusOfItemFromServer(itemType, itemId);
  // update grid in case lock status got change by someone else
  folderGridCtxt.gridSetCellValue(selectedId,gridLockColumn,fn_GetLockedCellFormatByStatusCode(folderGridCtxt,lockStatus));

  if (lockStatus === 1) {return "";}  
  if (lockStatus === 0) {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_row_must_be_locked_for_action");}

  return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.folder_row_locked_by_other_no_permission_action");
};

//---------------
fn_checkIfAttachmentsOnThisFolderAllowed = function (folderItemId) {
  var itm = top.aras.newIOMItem("","");
  itm.loadAML("<Item type='Item Folder' action='get' serverEvents='0' id='"+folderItemId+"' select='status,allow_sub_folder,allowed_relationships' />");
  itm = itm.apply();

  var allowedRelsMode = itm.getProperty("allowed_relationships","attachments_and_controlled_items");  //if prop not set use this default

  if (allowedRelsMode !== "" && allowedRelsMode.indexOf("attachments") >= 0) {return "";}  // is allowed

  return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.adding_files_to_folder_not_allowed");
};

//---------------
fn_checkIfControlledItemsOnThisFolderAllowed = function (folderItemId) {
  var itm = top.aras.newIOMItem("","");
  itm.loadAML("<Item type='Item Folder' action='get' serverEvents='0' id='"+folderItemId+"' select='status,allow_sub_folders,allowed_relationships' />");
  itm = itm.apply();

  var allowedRelsMode = itm.getProperty("allowed_relationships","attachments_and_controlled_items");  //if prop not set use this default

  if (allowedRelsMode !== "" && allowedRelsMode.indexOf("controlled_items") >= 0) {return "";}  // is allowed

  return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.adding_ctrld_items_to_folder_not_allowed");
};

//---------------
fn_checkIfSubFoldersOnThisFolderAllowed = function (folderItemId) {
  var itm = top.aras.newIOMItem("","");
  itm.loadAML("<Item type='Item Folder' action='get' serverEvents='0' id='"+folderItemId+"' select='status,allow_sub_folders,allowed_relationships' />");
  itm = itm.apply();

  var allowSubFolders = itm.getProperty("allow_sub_folders","");
  if (allowSubFolders === "") {allowSubFolders = "1";}  // if prop not set, then allow subfolders

  if (allowSubFolders === "1") {return "";}  // is allowed

  return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.adding_sub_folder_not_allowed");
};

//--------------
fn_ValidateActionRequest = function (folderGridCtxt, actionId, itemType, rowId) {
  // run various checks specific to action
  //debugger;

  //if (!isTopFolderParentItemLocked)
  // {return "Folder structure's parent item must be locked to do this action !";}

  var effectivityMustBeCurrent = false;
  var disallowForRootItems = false;  

  switch (actionId)
  {
    case "lock_row_item":
    case "unlock_row_item":
      effectivityMustBeCurrent = true;
      disallowForRootItems = true;
      if (itemType === folderGridCtxt.constants.folderGroupCtrldItemsRowType || itemType === folderGridCtxt.constants.folderGroupFilesRowType || itemType === "File")
       {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_rows_of_type_x") + itemType + " !";}

      break;
    case "add_new_controlled_item":
    case "add_existing_controlled_item":
      effectivityMustBeCurrent = true;
      disallowForRootItems = true;
      if (itemType !== "CTRLD_ITEMS_GROUP" && itemType !== "Item Folder")
       {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_rows_of_type_x") + itemType + " !";}

      break;
    case "view_folder":
      if (itemType !== "Item Folder")
       {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_rows_of_type_x") + itemType + " !";}
      break;

    case "dropFile":
    case "addFile":
    case "add_new_file_to_folder":
      effectivityMustBeCurrent = true;
      disallowForRootItems = true;
      if (itemType !== "FILES_GROUP" && itemType !== "Item Folder")
       {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_rows_of_type_x") + itemType + " !";}
      break;

    case "add_sub_folder":
      effectivityMustBeCurrent = true;
      disallowForRootItems = false;
      if (itemType !== "Item Folder")
       {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_onrows_of_type_x") + itemType + " !";}
      break;
  }

  if (disallowForRootItems) {
    // check rowId
    if (!folderGridCtxt.grid.getParentId(rowId))
      {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.action_not_allowed_on_root_rows");}
  }

  if (effectivityMustBeCurrent) {
    if (fn_getChoiceSelectionOnToolbar(folderGridCtxt.toolbar,"effectivity_mode") !== "current_config")
      {return top.aras.getResource("ItemFolders", "itemfoldertreegrid.message.effectivity_must_be_current_for_action");}
  }

  // success
  return "";
};
//------ END: validation functions

//------ BEGIN: local helper functions
fn_getInputRowVals = function (folderGridCtxt) {
    folderGridCtxt.inputRowTdVals = new Array(); // set in global variable
    for (var i=0; i<folderGridCtxt.numberOfGridColumns; i++)
    {
      var cellVal = folderGridCtxt.grid.cells("input_row", i).getValue();
      if (!cellVal) {cellVal = "";}
      folderGridCtxt.inputRowTdVals.push(cellVal);
    }
};
//--------
fn_setInputRowVals = function (folderGridCtxt) {
    if (!folderGridCtxt.inputRowTdVals) {return;}
	for (var i = 0; i < folderGridCtxt.inputRowTdVals.length; i++)
	{
		var elem = folderGridCtxt.inputRowTdVals[i];
		var cell = folderGridCtxt.grid.cells("input_row", elem.colId);
		cell.setValue(elem.val);
	}
};
//--------
fn_clearInputRowVals = function (folderGridCtxt) {
    if (!folderGridCtxt.inputRowTdVals) {return;}
	for (var i = 0; i < folderGridCtxt.inputRowTdVals.length; i++)
	{
		var elem = folderGridCtxt.inputRowTdVals[i];
		var cell = folderGridCtxt.grid.cells("input_row", elem.colId);
		cell.setValue("");
	}
};
//-----------------
fn_InsertControlledItemsGroupRowToTreeGrid = function (folderGridCtxt, parentNodeId, sourceItemId) {
  var groupNodeRowId = sourceItemId + "-" + folderGridCtxt.constants.folderGroupCtrldItemsRowType;
  if (folderGridCtxt.arasRelease === "Aras9") {
   if (folderGridCtxt.grid.isItemExists(groupNodeRowId)) {return groupNodeRowId;}
  }

  // add summary row for documents as child of Folder row
  var vals = [];
  vals[0] = folderGridCtxt.constants.folderGroupCtrldItems_label+":";
  for (var v = 1; v < folderGridCtxt.numberOfGridColumns; v++) {vals[v] = "";}
  var gridRow =  vals.join("|");
  folderGridCtxt.grid.insertNewChild(parentNodeId, groupNodeRowId, gridRow, groupNodeRowId, folderGridCtxt.iconsOpen[folderGridCtxt.constants.folderGroupCtrldItemsRowType], folderGridCtxt.icons[folderGridCtxt.constants.folderGroupCtrldItemsRowType]);
  folderGridCtxt.setGridRowUserData(groupNodeRowId,"gridData_rowItemType",folderGridCtxt.constants.folderGroupCtrldItemsRowType);

  // ColorCode parent row 
  folderGridCtxt.setGridRowUserData(parentNodeId,"gridData_hasFolderDetails","1");
  fn_SetBackgroundColorInRow(folderGridCtxt.grid,groupNodeRowId,0,folderGridCtxt.numberOfGridColumns-1, "#eeeeee",false);
  return groupNodeRowId;
};
//-----------------
fn_InsertFilesGroupRowToTreeGrid = function (folderGridCtxt, parentNodeId, sourceItemId ) {
  var groupNodeRowId = sourceItemId + "-" + folderGridCtxt.constants.folderGroupFilesRowType;
  if (folderGridCtxt.arasRelease === "Aras9") {
   if (folderGridCtxt.grid.isItemExists(groupNodeRowId)) {return groupNodeRowId;}
  }

  // add summary row for files as child of Folder row
  var vals = [];
  vals[0] = folderGridCtxt.constants.folderGroupFiles_label+":";
  for (var v = 1; v < folderGridCtxt.numberOfGridColumns; v++) {vals[v] = "";}
  var gridRow =  vals.join("|");
  folderGridCtxt.grid.insertNewChild(parentNodeId, groupNodeRowId, gridRow, groupNodeRowId, folderGridCtxt.iconsOpen[folderGridCtxt.constants.folderGroupFilesRowType], folderGridCtxt.icons[folderGridCtxt.constants.folderGroupFilesRowType]);
  folderGridCtxt.setGridRowUserData(groupNodeRowId,"gridData_rowItemType",folderGridCtxt.constants.folderGroupFilesRowType);

  // ColorCode parent row 
  folderGridCtxt.setGridRowUserData(parentNodeId,"gridData_hasFolderDetails","1");
  fn_SetBackgroundColorInRow(folderGridCtxt.grid,groupNodeRowId,0,folderGridCtxt.numberOfGridColumns-1, "#eeeeee",false);

  return groupNodeRowId;
};
//-----------------
fn_SetBackgroundColorInRow = function (gridCtrl, row_id, startCell, endCell, colorCode, upToRoot) {
  if (!upToRoot || upToRoot === undefined) {upToRoot = false;}
  fn_SetBackgroundColorOfCellsInRow(gridCtrl, row_id, startCell, endCell, colorCode);
  // include one parent level
  var parent_row_id = gridCtrl.getParentId(row_id);
  if (parent_row_id && parent_row_id.indexOf("ROOT") < 0) {fn_SetBackgroundColorOfCellsInRow(gridCtrl, parent_row_id, startCell, endCell, colorCode);}

  if (!upToRoot) {return;}
  parent_row_id = gridCtrl.getParentId(parent_row_id);
  while (parent_row_id)
  {
    if (parent_row_id.indexOf("ROOT") < 0)
      {fn_SetBackgroundColorOfCellsInRow(gridCtrl, parent_row_id, startCell, endCell, colorCode);}
    parent_row_id = gridCtrl.getParentId(parent_row_id);
  }
  return;
};
